sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  if (inputs.statusCode == '201'){
    var resbody = JSON.parse(inputs.resbody);
    outputs.id = resbody.id
    outputs.job = resbody.job
    outputs.status = "Ansible job started successfully";
   }
  else {
    outputs.status = "There is a error from job template in ansible tower with errorcode = " + inputs.statusCode ;
  }
})(inputs, outputs);

