sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  gs.info('read' + inputs.reqreadrr);
  //////////////////// read users //////////////////////////////////
   if (inputs.reqreadrr) 
     {
       gs.info('reqread');
        var vales2 = inputs.reqreadrr;
  	    var Auser_id2 = '';
        var sys2 = [];
     	var values2 = vales2.split(',');
     	var length2 = values2.length;
        if (inputs.readgrprr)
        {
        	outputs.readgroup_prr = inputs.readgrprr
        }
        else
        {
            gs.info('NA');
        	outputs.readgroup_prr = "NA"
        }
 		for(var k=0;k<length2;k++)
        {
          	gs.info('For in K')
      		var usersysid2 = values2[k];//contains single values
      		var userrecord2 = new GlideRecord('sys_user');
  	 		userrecord2.addQuery('sys_id',usersysid2); //Query sys id
     		userrecord2.addQuery('active',true); // Active false user should not be added
     		userrecord2.query();
     		if(userrecord2.next()) 
            { 
     			var idv2 = userrecord2.user_name.getDisplayValue();
				if(k!=values2.length-1)//added this check eliminate , at the last
     			{
                  gs.info('k length -1')
                    Auser_id2 += ('"' + idv2 + '"' + ','); 
        		}
       			else
        		{
                  Auser_id2 += ('"' + idv2 + '"');
                  gs.info('test'+Auser_id2 );
        		}
			}	
    	}
  		outputs.addreadrr = Auser_id2;
       //gs.info('fianl' + addreadRR);
       gs.info('fianl1' + Auser_id2);

     }
     else 
     {
       gs.info('final else block');
     }
  outputs.addreadrr = Auser_id2;
  })(inputs, outputs);

