sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
     if (inputs.read) 
     {
        //outputs.check1 = 'running';
        outputs.readaccstatus = 'define';
        var vales = inputs.read;
  	    var ruser_id = '';
        var ruser_name = '';
    	var ruser_email = '';
     	var sys = [];
     	var values = vales.split(',');
     	var email = values.length;
  	 	gs.info("RRRR"+email);  
 		for(var k=0;k<email;k++)
        {
      		var usersysid = values[k];//contains single values
      		var userrecord = new GlideRecord('sys_user');
  	 		userrecord.addQuery('sys_id',usersysid); //Query sys id
     		userrecord.addQuery('active',true); // Active false user should not be added
     		userrecord.query();
     		if(userrecord.next()) 
            { 
     			//gr.get(values)
     			var idv = userrecord.user_name.getDisplayValue();
                var eid = userrecord.email.getDisplayValue();
            	var uname = userrecord.name.getDisplayValue();
     			gs.info("RRRR2=="+idv);
				gs.info("RRRR3=="+eid);
				gs.info("RRRR4=="+uname);
     			if(k!=values.length-1)//added this check eliminate , at the last
     			{
     				ruser_id += (idv + ',');
					ruser_email += (eid + ',');
					ruser_name += (uname + ',');

        		}
       			else
        		{
              		ruser_id += idv
					ruser_email += eid
					ruser_name += uname
        		}
			}	
    	}
  		gs.info("RRRR1=="+ruser_id);
  		outputs.readacc = ruser_id;
		gs.info("RRRR2=="+ruser_email);
  		outputs.ruseremail = ruser_email; 
    	gs.info("RRRR3=="+ruser_name);
  		outputs.runame = ruser_name;  
     }
     else 
     {
       outputs.readaccstatus = 'notdefine';
     }
     if (inputs.readwrite) 
     {
     	outputs.rwaccstatus = 'define';
        var vales = inputs.readwrite;
  		var rwuser_id = '';
		var rwuser_name = '';
    	var rwuser_email = '';	

    	var sys = [];
    	var values = vales.split(',');
    	var email = values.length;
  		gs.info("RRRR"+email);
 		for(var k=0;k<email;k++)
    	{
     		var usersysid = values[k];//contains single values
      		var userrecord = new GlideRecord('sys_user');
  			userrecord.addQuery('sys_id',usersysid); //Query sys id
     		userrecord.addQuery('active',true); // Active false user should not be added
     		userrecord.query();
     		if(userrecord.next()) 
        	{ 
     			//gr.get(values)
     			var idv = userrecord.user_name.getDisplayValue();
				var eid = userrecord.email.getDisplayValue();
            	var uname = userrecord.name.getDisplayValue();
     			gs.info("RRRR2=="+idv);
				gs.info("RRRR3=="+eid);
				gs.info("RRRR4=="+uname);
     			if(k!=values.length-1)//added this check eliminate , at the last
     			{
     				rwuser_id += (idv + ',');
					rwuser_email += (eid + ',');
					rwuser_name += (uname + ',');

        		}
       			else
        		{
              		rwuser_id += idv
					rwuser_email += eid
					rwuser_name += uname
        		}
	 		}
    	}
  		gs.info("RRRR1=="+rwuser_id);
  		outputs.rwacc = rwuser_id;
		gs.info("RRRR2=="+rwuser_email);
		outputs.rwuseremail = rwuser_email; 
		gs.info("RRRR3=="+rwuser_name);
		outputs.rwuname = rwuser_name;  				
     }
  	else
    {
       outputs.rwaccstatus = 'notdefine';
     }
     if (inputs.revoke) 
     {
     	outputs.revokeaccstatus = 'define';
     	var vales = inputs.revoke;
  	    var reuser_id = '';
		var reuser_name = '';
    	var reuser_email = '';	
     	var sys = [];
     	var values = vales.split(',');
     	var email = values.length;
  	 	gs.info("RRRR"+email);  
 		for(var k=0;k<email;k++)
        {
      		var usersysid = values[k];//contains single values
      		var userrecord = new GlideRecord('sys_user');
  	 		userrecord.addQuery('sys_id',usersysid); //Query sys id
     		userrecord.addQuery('active',true); // Active false user should not be added
     		userrecord.query();
     		if(userrecord.next()) 
            { 
     			//gr.get(values)
     			var idv = userrecord.user_name.getDisplayValue();
				var eid = userrecord.email.getDisplayValue();
            	var uname = userrecord.name.getDisplayValue();
     			gs.info("RRRR2=="+idv);
				gs.info("RRRR3=="+eid);
				gs.info("RRRR4=="+uname);
     			if(k!=values.length-1)//added this check eliminate , at the last
     			{
     				reuser_id += (idv + ',');
					reuser_email += (eid + ',');
