        		}
       			else
        		{
              		reuser_id += idv
					reuser_email += eid
					reuser_name += uname
        		}
	 		}
    	}
  		gs.info("RRRR1=="+reuser_id);
  		outputs.revokeacc = reuser_id;
        gs.info("RRRR2=="+reuser_email);
		outputs.reuseremail = reuser_email; 
		gs.info("RRRR3=="+reuser_name);
		outputs.reuname = reuser_name;  				
    }
    else
    {
		 outputs.revokeaccstatus = 'notdefine';
    }
})(inputs, outputs);

