sys_variable_value.value:
try
{
    $Failed = @()
$membersR = @()

$encrypted1 = Get-Content 'E:\Scripts\WPS\Software_Provision_Prod\pass1.txt'
$srvuser1 = "wbi\sccmauto-srv" 
$password1 = ConvertTo-SecureString -string $encrypted1
$cred1 = new-object -typename System.Management.Automation.PSCredential -argumentlist $srvuser1,$password1

#################
$encrypted = Get-Content 'E:\Scripts\WPS\Software_Provision_Prod\iexec.txt'
$user = "wbi\iexecute_svc" 
$password = ConvertTo-SecureString -string $encrypted 
$cred = new-object -typename System.Management.Automation.PSCredential -argumentlist $user,$password

$encryptedF = Get-Content 'E:\Scripts\WPS\Software_Provision_Prod\iexec.txt'
$userF = "fsl\iexecute_svc" 
$passwordF = ConvertTo-SecureString -string $encryptedF
$credF = new-object -typename System.Management.Automation.PSCredential -argumentlist $userF,$passwordF

$a = Invoke-Command -ComputerName 'twkhh01wsustw1v.wbi.nxp.com'  -credential $cred -ConfigurationName Ipswitch -ScriptBlock { 
param($group_read,$group_write,$users_readw, $users_writew,$FolderName,$cred1,$remusers_read, $credF)
try{
$Innuser = @()  ## REvoke var
$innuser1 = @()  ## REvoke var
$innGroup = @()  ## REvoke var
$inngroup1 = @()  ## REvoke var
if($users_readw)
{
    $users_read  = $users_readw.split(",")
    if($group_read -eq 'NA')
    {

        $Acl1 = Get-Acl $FolderName
        foreach($user_read in $users_read)
        { 
            $FileSystemRights = [System.Security.AccessControl.FileSystemRights]"Read,ReadAndExecute"
            $AccessControlType = [System.Security.AccessControl.AccessControlType]::Allow
            $InheritanceFlags = [System.Security.AccessControl.InheritanceFlags]"ContainerInherit, ObjectInherit"
            $PropagationFlags = [System.Security.AccessControl.PropagationFlags]"NoPropagateInherit" 
            $AccessRule = New-Object System.Security.AccessControl.FileSystemAccessRule ($user_read, $FileSystemRights, $InheritanceFlags, $PropagationFlags, $AccessControlType)
$Acl1.SetAccessRule($AccessRule)
Set-Acl -Path $FolderName -AclObject $acl1 -ErrorAction SilentlyContinue
            #$NewAccessRule = New-Object system.security.accesscontrol.filesystemaccessrule($user_read,"Read,ReadAndExecute","Allow")
            #$Acl1.SetAccessRule($NewAccessRule)
            #Set-Acl -Path $FolderName -AclObject $acl1
        }
        $membersR1 = ((Get-Acl $FolderName).Access).identityreference |select -ExpandProperty value
        foreach($memberR in $membersR1)
        {
            $member = $memberR.split('\')
            $membersR += $member
        }
        foreach($user_read in $users_read)
        {
            if($membersR -contains $user_read)
            {
                $success = 'Succ'
            }
            else
            {
                $Failed += $user_read
            }
        }
    }
    else
    {        
        #Write-Host 'test'
        #Write-Host $cred1
        if($FolderName -like '*.freescale.net\*')
        {
        Add-ADGroupMember -Identity $group_read -Members $users_read -Server fsl.freescale.net -Credential $CredF  -Confirm:$false 
        #$cred1
        $membersR = Get-ADGroupMember -Identity $group_read -server fsl.freescale.net | Select -ExpandProperty samaccountname
        }
        else
        {
        Add-ADGroupMember -Identity $group_read -Members $users_read -Server wbi.nxp.com -Credential $Cred1  -Confirm:$false 
        #$cred1
        $membersR = Get-ADGroupMember -Identity $group_read -server wbi.nxp.com  | Select -ExpandProperty samaccountname
        }
        foreach($user_read in $users_read)
        {
            If ($membersR -contains "$user_read") 
            {
                $success = 'Succ'
            }
            else
            {
                $Failed +=  $user_read
            }
        }
    }
}   
if($users_writew)
{
    $users_write     = $users_writew.split(",")
    if($group_write -eq 'NA')
    {
        $Acl1 = Get-Acl $FolderName
        foreach($user_write in $users_write)
        {
            $FileSystemRights = [System.Security.AccessControl.FileSystemRights]"Read,ReadAndExecute,write,Modify"
            $AccessControlType = [System.Security.AccessControl.AccessControlType]::Allow
            $InheritanceFlags = [System.Security.AccessControl.InheritanceFlags]"ContainerInherit, ObjectInherit"
            $PropagationFlags = [System.Security.AccessControl.PropagationFlags]"NoPropagateInherit" 
            $AccessRule = New-Object System.Security.AccessControl.FileSystemAccessRule ($user_write, $FileSystemRights, $InheritanceFlags, $PropagationFlags, $AccessControlType)
            $Acl1.SetAccessRule($AccessRule)
            Set-Acl -Path $FolderName -AclObject $acl1 -ErrorAction SilentlyContinue

            #$NewAccessRule = New-Object system.security.accesscontrol.filesystemaccessrule($user_write,"Read,ReadAndExecute,write,Modify","Allow")
            #$Acl1.SetAccessRule($NewAccessRule)
            #Set-Acl -Path $FolderName -AclObject $acl1
        }
        $membersW1 = ((Get-Acl $FolderName).Access).identityreference |select -ExpandProperty value
        foreach($memberw in $membersw1)
        {
            $member = $memberw.split('\')
            $membersw += $member
        }

        foreach($user_write in $users_write)
        {
            if($membersW -contains $user_write)
            {
                $success = 'Succ'
            }
            else
            {
                $Failed += $user_write
            }
        }
    }
    else
    {
        if($FolderName -like '*.freescale.net\*')
        {
        Add-ADGroupMember -Identity $group_write -Members $users_write -Server fsl.freescale.net  -Credential $CredF -Confirm:$false 
        #$cred1
        $membersW = Get-ADGroupMember -Identity $group_write -server fsl.freescale.net| Select -ExpandProperty samaccountname
        }
        else
        {
        Add-ADGroupMember -Identity $group_write -Members $users_write -Server wbi.nxp.com -Credential $Cred1  -Confirm:$false 
        $membersW = Get-ADGroupMember -Identity $group_write -server wbi.nxp.com  | Select -ExpandProperty samaccountname
        }
        foreach($user_write in $users_write)
        {
            If ($membersW -contains "$user_write") 
            {
                $success = 'Succ'
            }
            else
            {
                $Failed +=   $user_write
            }
        }
    }
}


if($remusers_read)
{
    $users_read  = $remusers_read.split(",")
    $Acl = Get-Acl $FolderName
    $access1 = $acl.access | Where-Object { (-not $_.IsInherited) -and $_.IdentityReference -like "wbi\*" }
    foreach($each in $access1)
    {
        $RRusers = ($each |select -ExpandProperty identityreference).value
        $RRuserDomainsplit = $RRusers.split('\')
        $RRuser = $RRuserDomainsplit[1]
        $ADFetch = Get-ADObject -Filter "SamAccountName -eq '$RRuser'"
        $SamType = $ADFetch.objectclass
        if($SamType -eq 'user')
        {
            $Innuser +=  $each.IdentityReference    # with wbi\
            $innuser1 += $RRuser                    # without WBI
        }
        elseif($SamType -eq 'group')
        {
            $innGroup += $each.IdentityReference
            $inngroup1 += $RRuser
        }
        else
        {
         #   Write-Host $each.IdentityReference is not remove
        }
    }
######Removing user from ACL properties foreach($remuser_read in $remusers_read)
    $formcount = $Innuser.count
    $i = 0
    for($i = 0; $i -le $formcount; $i++)
    {
        #if($Innuser[$i] -contains $remuser_read)
        $remusers_reads = $remusers_read.Split(',')
        if($remusers_reads -contains $Innuser1[$i])
        {
            $Acl1 = Get-Acl $FolderName
            $rules = $acl1.access | Where-Object { (-not $_.IsInherited) -and $_.IdentityReference -like "wbi\*" }
            ForEach($rule in $rules) 
            {
                if($rule.identityreference -eq $innuser[$i])
                {
                    $acl1.RemoveAccessRule($rule) | Out-Null
                    Set-ACL -Path "$FolderName" -AclObject $acl1 
                }
                else
                {
                }

            }
        }
        if($?)
        {$success = 'succ'        }
        else
        {
            $Failed += 'Failure during revoke access'
        }
    }
######Removing user from group properties
    if($inngroup1)
    {
        $Remuses_read = $Remusers_read.Split(',')
        foreach($inngrou in $inngroup1)
        {
            $members = Get-ADGroupMember -Identity $inngrou |select -ExpandProperty Samaccountname
            $memcount = $Remuses_read.count
            for($k = 0;$k -lt $memcount; $k++)
            {
                if($members -contains $Remuses_read[$k])
                {
                    if($FolderName -like '*.freescale.net\*')
                    {
                    $kk = Remove-ADGroupMember -Identity $inngrou -Members $Remuses_read[$k] -Server fsl.freescale.net -Credential $credF -Confirm:$false -ErrorAction SilentlyContinue
                    }
                    else
                    {
                    $kk = Remove-ADGroupMember -Identity $inngrou -Members $Remuses_read[$k] -Server wbi.nxp.com -Credential $cred1 -Confirm:$false -ErrorAction SilentlyContinue
                    }
                    if($?)
                    {$success += 'succ'        }
                    else
                    {
                        $failed += "failure during revoke access $Remuses_read[$k]"
                    }
                }
            }
        }
    }
}   
if($Failed)
{
    Write-Host "Failure users $failed"
}
else
{
    Write-Host " Success $success"
}
}
catch
{
    Write-Host 'failure'
    $error
}
} -argumentlist $group_read,$group_write,$users_readw, $users_writew,$FolderName,$cred1,$remusers_read,$credF
}
catch
{
    $error
    Write-Host 'Failure in execution'
}

