sys_variable_value.value:
(function execute(inputs, outputs) {
    var vales = inputs.ids;
  	var user_id = '';
    var user_name = '';
    var user_email = '';
    var sys = [];
    var values = vales.split(',');
    var email = values.length;
  	gs.info("RRRR"+email);
 	for(var k=0;k<email;k++)
    {
     	var usersysid = values[k];//contains single values
      	var userrecord = new GlideRecord('sys_user');
  		userrecord.addQuery('sys_id',usersysid); //Query sys id
     	userrecord.addQuery('active',true); // Active false user should not be added
     	userrecord.query();
     	if(userrecord.next()) 
        { 
     		//gr.get(values)
     		var idv = userrecord.user_name.getDisplayValue();
            var eid = userrecord.email.getDisplayValue();
            var uname = userrecord.name.getDisplayValue();
     		gs.info("RRRR2=="+idv);
     		if(k!=values.length-1)//added this check eliminate , at the last
     		{
     			user_id += (idv + ',');
            }
       		else
            {
              	user_id += idv
            }
			gs.info("RRRR3=="+eid);
     		if(k!=values.length-1)//added this check eliminate , at the last
     		{
     			user_email += (eid + ',');
            }
       		else
            {
              	user_email += eid
            }
      		gs.info("RRRR4=="+uname);
     		if(k!=values.length-1)//added this check eliminate , at the last
     		{
     			user_name += (uname + ',');
            }
       		else
            {
              	user_name += uname
            }
	 	}
    }
  	gs.info("RRRR1=="+user_id);
  	outputs.userid = user_id; 
    gs.info("RRRR2=="+user_email);
  	outputs.useremail = user_email; 
    gs.info("RRRR3=="+user_name);
  	outputs.uname = user_name;  
})(inputs, outputs);

