sys_variable_value.value:
write-host $env:computername
Write-Host "Read access" + $readacc "write access" +$rwacc "Revokce access" + $revokeacc
Write-Host "Read access" + $readaccstatus "write access" +$rwaccstatus "Revokce access" + $revokeaccstatus
if($readaccstatus -eq 'define')
{
    Write-Host 'read define'
}
else
{
    Write-Host 'read not define'
}
if($rwaccstatus -eq 'define')
{
    Write-Host 'rw define'
}
else
{
    Write-Host 'rw not define'
}
if($revokeaccstatus -eq 'define')
{
    Write-Host 'revoke define'
}
else
{
    Write-Host 'revoke not define'
}

