sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
  if (inputs.read) 
{
    var vales = inputs.read;
    var ruser_id = '';

    var Rusers = inputs.Number.variables.Read_Only_Access.toString();
    var Rsplit = Rusers.split(',');
    var Rlength = Rsplit.length;
    gs.info('length' + Rlength);

	if((Rlength == 1) || (Rlength == 32))
	{
		var Ridv = vales.user_name.getDisplayValue();
        ruser_id += (Ridv);
	}
	else
	{
		while(vales.next())
		{
			var Ridv = vales.user_name.getDisplayValue();
			ruser_id += (Ridv + ',');
		}
	}
	gs.info("ruser_id=="+ruser_id);
	var readacc1 = ruser_id.trim().replace(/,{1,}$/, '');
	outputs.readacc = readacc1;
}
else 
{
  gs.info('read users not defined for GAF');
}

/// Readwrite Access for GAF share
 if (inputs.readwrite) 
{
    var Wvales = inputs.readwrite;
    var Wuser_id = '';

    var Wusers = inputs.Number.variables.Read_Write_Access.toString();
    var Wsplit = Wusers.split(',');
    var Wlength = Wsplit.length;
	if((Wlength == 1) || (Wlength == 32))
	{
		var Widv = Wvales.user_name.getDisplayValue();
        Wuser_id += (Widv);
	}
	else
	{
		while(Wvales.next())
		{
			var Widv = Wvales.user_name.getDisplayValue();
			Wuser_id += (Widv + ',');
		}
	}
	var Writeacc1 = Wuser_id.trim().replace(/,{1,}$/, '');
	outputs.rwacc = Writeacc1;
}
else 
{
  //outputs.readaccstatus = 'notdefine';
  gs.info('Write users not defined for GAF');
}
// Revoke read write access
 if (inputs.revoke) 
{
    var Revvales = inputs.revoke;
    var Revuser_id = '';

    var Revusers = inputs.Number.variables.people_to_revoke_read_write_access.toString();
    var Revsplit = Revusers.split(',');
    var Revlength = Revsplit.length;
	if((Revlength == 1) || (Revlength == 32))
	{
