	else
	{
		while(Revvales.next())
		{
			var Revidv = Revvales.user_name.getDisplayValue();
			Revuser_id += (Revidv + ',');
		}
	}
	var Revacc1 = Revuser_id.trim().replace(/,{1,}$/, '');
	outputs.revokeacc = Revacc1;
}
else 
{
  //outputs.readaccstatus = 'notdefine';
  gs.info('Revoke users not defined for GAF');
}
