sys_script_include.script:
var NXPReferenceQualifierForShareFolder = Class.create();
NXPReferenceQualifierForShareFolder.prototype = {
    initialize: function() {
    },

	getShareDetails: function(siteid,base)
	{
		//gs.info('callinggetShareDetails');
		var sitenamedetails=[];
		var uniqueshare = [];
				var Ssitenamedetails=new GlideRecord('cmdb_ci_storage_fileshare');
			Ssitenamedetails.addQuery('location',siteid);
			Ssitenamedetails.addQuery('u_base_share',base);
				Ssitenamedetails.query();
				//gs.info('zzzz2');
					while(Ssitenamedetails.next()){		
						sitenamedetails.push(Ssitenamedetails.sys_id.toString());


					//gs.info('zzzz3' +sitenamedetails);

					}		
					return "sys_idIN"+sitenamedetails.join(',');

	},

    type: 'NXPReferenceQualifierForShareFolder'
};
